import React, { useState, useEffect } from 'react';
import { Box } from '@mui/material';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';
import UploadReportCard from '../components/UploadReportCard';
import ReportsTable from '../components/ReportsTable';
import { useAuth } from '../contexts/AuthContext';
import { getMyReports, getAllReports } from '../services/api';

export default function DashboardPage() {
  const [selectedMenu, setSelectedMenu] = useState('Dashboard');
  const [reports, setReports] = useState([]);
  const [filters, setFilters] = useState({ date: '', status: '' });
  const [sorting, setSorting] = useState({ field: 'createdAt', direction: 'desc' });
  const { user } = useAuth();

  const fetchReports = async () => {
    if (!user) return;
    try {
      const params = { ...filters, sortBy: sorting.field, sortOrder: sorting.direction };
      const data = user.role === 'manager'
        ? await getAllReports(params)
        : await getMyReports(params);
      setReports(data);
    } catch (error) {
      console.error("Failed to fetch reports:", error);
      setReports([]);
    }
  };

  useEffect(() => {
    fetchReports();
  }, [user, filters, sorting]);

  // Logic to determine what to render based on selectedMenu is no longer needed here
  // as the navigation is handled by the router. This component is now only for the dashboard view.

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh' }}>
      <Sidebar selected={selectedMenu} onSelect={item => setSelectedMenu(item.text)} />
      <Box sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
        <Header title="Dashboard" />
        <Box sx={{ p: 3, flexGrow: 1, bgcolor: '#f5f5f5' }}>
          <UploadReportCard onUpload={fetchReports} />
          <ReportsTable
            reports={reports}
            filters={filters}
            setFilters={setFilters}
            fetchReports={fetchReports}
            sorting={sorting}
            setSorting={setSorting}
          />
        </Box>
      </Box>
    </Box>
  );
}
