import React, { useState, useEffect } from 'react';
import { Box } from '@mui/material';
import UploadReportCard from '../components/UploadReportCard';
import ReportsTable from '../components/ReportsTable';
import { useAuth } from '../contexts/AuthContext';
import { getMyReports, getAllReports } from '../services/api';

export default function DashboardPage() {
  const [reports, setReports] = useState([]);
  const [filters, setFilters] = useState({ date: '', status: '' });
  const [sorting, setSorting] = useState({ field: 'createdAt', direction: 'desc' });
  const [page, setPage] = useState(0); // MUI TablePagination is 0-indexed
  const [totalReports, setTotalReports] = useState(0);
  const { user } = useAuth();
  
  const ROWS_PER_PAGE = 10;

  const fetchReports = async () => {
    if (!user) return;
    try {
      const params = {
        ...filters,
        sortBy: sorting.field,
        sortOrder: sorting.direction,
        page: page + 1, // API is 1-indexed
        size: ROWS_PER_PAGE,
      };
      const data = user.role === 'manager'
        ? await getAllReports(params)
        : await getMyReports(params);
      
      setReports(data.reports);
      setTotalReports(data.totalItems);
    } catch (error) {
      console.error("Failed to fetch reports:", error);
      setReports([]);
      setTotalReports(0);
    }
  };
  
  const handleUploadSuccess = () => {
    // After upload, go to the first page to see the new report.
    // This will trigger the useEffect to refetch reports.
    if (page !== 0) {
      setPage(0);
    } else {
      // If already on page 0, the state change won't trigger the effect,
      // so we need to call fetchReports manually.
      fetchReports();
    }
  };

  useEffect(() => {
    fetchReports();
  }, [user, filters, sorting, page]);

  return (
    <>
      <UploadReportCard onUpload={handleUploadSuccess} />
      <ReportsTable
        reports={reports}
        filters={filters}
        setFilters={setFilters}
        fetchReports={fetchReports}
        sorting={sorting}
        setSorting={setSorting}
        page={page}
        setPage={setPage}
        rowsPerPage={ROWS_PER_PAGE}
        totalReports={totalReports}
      />
    </>
  );
}
