import React, { useState, useEffect } from 'react';
import { Box } from '@mui/material';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';
import UploadReportCard from '../components/UploadReportCard';
import ReportsTable from '../components/ReportsTable';
import { useAuth } from '../contexts/AuthContext';
import { getAllReports, getMyReports } from '../services/api';

export default function DashboardPage() {
  const [selectedMenu, setSelectedMenu] = useState('Dashboard');
  const [reports, setReports] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    if (!user) return;
    if (user.role === 'manager') {
      getAllReports({}).then(setReports);
    } else {
      getMyReports().then(setReports);
    }
  }, [user]);

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh' }}>
      <Sidebar selected={selectedMenu} onSelect={item => setSelectedMenu(item.text)} />
      <Box sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
        <Header title={selectedMenu} />
        <Box sx={{ p: 3, flexGrow: 1, bgcolor: '#f5f5f5' }}>
          {selectedMenu === 'Dashboard' && (
            <>
              <UploadReportCard />
              <ReportsTable reports={reports} />
            </>
          )}
          {selectedMenu === 'My Reports' && (
            <div>My Reports (to be implemented)</div>
          )}
          {selectedMenu === 'Settings' && (
            <div>Settings (to be implemented)</div>
          )}
        </Box>
      </Box>
    </Box>
  );
}
