import React, { useState } from 'react';
import {
  Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  FormControl, InputLabel, Select, MenuItem, Box, Button
} from '@mui/material';
import { useAuth } from '../contexts/AuthContext';
import { getAllReports } from '../services/api';
import ReportDetailModal from './ReportDetailModal';

const statusOptions = ['Submitted', 'Reviewed', 'Rejected'];
const nameOptions = ['Alice', 'Bob', 'Charlie'];
const dateOptions = ['2024-06-01', '2024-06-02', '2024-06-03'];

function getEmployeeName(r) {
  const user = r.User || r.user;
  if (!user) return 'N/A';
  if (user.firstName && user.lastName) return `${user.firstName} ${user.lastName}`;
  if (user.dataValues && user.dataValues.firstName && user.dataValues.lastName) return `${user.dataValues.firstName} ${user.dataValues.lastName}`;
  return 'N/A';
}

export default function ReportsTable({ reports, setReports }) {
  const [filters, setFilters] = useState({ name: '', date: '', status: '' });
  const { user } = useAuth();
  const [selectedReport, setSelectedReport] = useState(null);

  React.useEffect(() => {
    if (user && user.role === 'manager') {
      getAllReports(filters).then(setReports);
    }
  }, [filters, user, setReports]);

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };
  
  const handleViewClick = (report) => {
    setSelectedReport(report);
  };

  const handleCloseModal = () => {
    setSelectedReport(null);
  };

  return (
    <>
      <Paper>
        {user && user.role === 'manager' && (
          <Box sx={{ display: 'flex', gap: 2, p: 2 }}>
            {/* Filter controls */}
          </Box>
        )}
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Employee</TableCell>
                <TableCell>Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>View</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {reports.map((r) => (
                <TableRow key={r.id}>
                  <TableCell>{getEmployeeName(r)}</TableCell>
                  <TableCell>
                    {r.createdAt
                      ? new Date(r.createdAt).toLocaleString('tr-TR')
                      : r.date}
                  </TableCell>
                  <TableCell>{r.status || 'Submitted'}</TableCell>
                  <TableCell>
                    <Button onClick={() => handleViewClick(r)}>View</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
      {selectedReport && (
        <ReportDetailModal
          report={selectedReport}
          onClose={handleCloseModal}
        />
      )}
    </>
  );
}
