import React, { useState, useEffect } from 'react';
import {
  Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  FormControl, InputLabel, Select, MenuItem, Box, Button
} from '@mui/material';
import { useAuth } from '../contexts/AuthContext';
import { getAllReports } from '../services/api';
import ReportDetailModal from './ReportDetailModal';

const statusOptions = ['Submitted', 'Reviewed', 'Rejected'];
const nameOptions = ['Alice', 'Bob', 'Charlie'];
const dateOptions = ['2024-06-01', '2024-06-02', '2024-06-03'];

export default function ReportsTable({ reports, setReports }) {
  const [filters, setFilters] = useState({ name: '', date: '', status: '' });
  const { user } = useAuth();
  const [selectedReport, setSelectedReport] = useState(null);

  useEffect(() => {
    // Only managers can filter all reports
    if (user && user.role === 'manager') {
      getAllReports(filters).then(setReports);
    }
  }, [filters, user, setReports]);

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };
  
  const handleViewClick = (report) => {
    setSelectedReport(report);
  };

  const handleCloseModal = () => {
    setSelectedReport(null);
  };

  return (
    <>
      <Paper>
        {user && user.role === 'manager' && (
          <Box sx={{ display: 'flex', gap: 2, p: 2 }}>
            <FormControl sx={{ minWidth: 120 }}>
              <InputLabel>Name</InputLabel>
              <Select
                name="name"
                value={filters.name}
                label="Name"
                onChange={handleChange}
              >
                <MenuItem value="">All</MenuItem>
                {nameOptions.map((n) => (
                  <MenuItem key={n} value={n}>{n}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl sx={{ minWidth: 120 }}>
              <InputLabel>Date</InputLabel>
              <Select
                name="date"
                value={filters.date}
                label="Date"
                onChange={handleChange}
              >
                <MenuItem value="">All</MenuItem>
                {dateOptions.map((d) => (
                  <MenuItem key={d} value={d}>{d}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl sx={{ minWidth: 120 }}>
              <InputLabel>Status</InputLabel>
              <Select
                name="status"
                value={filters.status}
                label="Status"
                onChange={handleChange}
              >
                <MenuItem value="">All</MenuItem>
                {statusOptions.map((s) => (
                  <MenuItem key={s} value={s}>{s}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
        )}
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Employee</TableCell>
                <TableCell>Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>View</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {reports.map((r) => (
                <TableRow key={r.id}>
                  <TableCell>{r.User?.email || 'N/A'}</TableCell>
                  <TableCell>{r.date}</TableCell>
                  <TableCell>{r.status || 'Submitted'}</TableCell>
                  <TableCell>
                    <Button onClick={() => handleViewClick(r)}>View</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
      {selectedReport && (
        <ReportDetailModal
          report={selectedReport}
          onClose={handleCloseModal}
        />
      )}
    </>
  );
}
