import React from 'react';
import {
  Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  FormControl, InputLabel, Select, MenuItem, Box, Button, TextField,
  IconButton
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { useAuth } from '../contexts/AuthContext';
import { getAllReports } from '../services/api';
import ReportDetailModal from './ReportDetailModal';
import EditReportModal from './EditReportModal'; // Import the new modal
import { deleteReport, updateReport } from '../services/api'; // Import api functions

const statusOptions = ['Submitted', 'Reviewed', 'Rejected'];
const nameOptions = ['Alice', 'Bob', 'Charlie'];
const dateOptions = ['2024-06-01', '2024-06-02', '2024-06-03'];

function getEmployeeName(r) {
  const user = r.User || r.user;
  if (!user) return 'N/A';
  if (user.firstName && user.lastName) return `${user.firstName} ${user.lastName}`;
  if (user.dataValues && user.dataValues.firstName && user.dataValues.lastName)
    return `${user.dataValues.firstName} ${user.dataValues.lastName}`;
  // Log the user object for further debugging
  console.log('User object:', user);
  return 'N/A';
}

export default function ReportsTable({ reports, filters, setFilters, setReports, fetchReports }) { // Renamed prop for clarity
  const { user } = useAuth();
  const [selectedReport, setSelectedReport] = React.useState(null);
  const [editingReport, setEditingReport] = React.useState(null); // State for edit modal

  React.useEffect(() => {
    // Only managers can filter all reports via this component
    if (user && user.role === 'manager') {
      getAllReports(filters).then(setReports);
    }
  }, [filters, user, setReports]);

  const handleChange = (e) => {
    setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };
  
  const handleViewClick = (report) => {
    setSelectedReport(report);
  };

  const handleCloseModal = () => {
    setSelectedReport(null);
  };
  
  const handleDelete = async (reportId) => {
    if (window.confirm('Are you sure you want to delete this report?')) {
      try {
        await deleteReport(reportId);
        fetchReports(); // Refresh the list
      } catch (error) {
        console.error('Failed to delete report:', error);
        alert('Could not delete the report.');
      }
    }
  };
  
  const handleUpdate = async (reportId, newNotes) => {
    try {
      await updateReport(reportId, newNotes);
      setEditingReport(null); // Close modal on success
      fetchReports(); // Refresh the list
    } catch (error) {
      console.error('Failed to update report:', error);
      alert('Could not update the report.');
    }
  };

  return (
    <>
      <Paper>
        <Box sx={{ display: 'flex', gap: 2, p: 2, alignItems: 'center' }}>
          {/* Manager-specific filter can be added here later */}
          <TextField
            label="Filter by Date"
            name="date"
            type="date"
            value={filters.date}
            onChange={handleChange}
            InputLabelProps={{ shrink: true }}
            sx={{ maxWidth: 180 }}
          />
          <FormControl sx={{ minWidth: 150 }}>
            <InputLabel>Status</InputLabel>
            <Select
              name="status"
              value={filters.status}
              label="Status"
              onChange={handleChange}
            >
              <MenuItem value=""><em>All Statuses</em></MenuItem>
              {statusOptions.map((s) => (
                <MenuItem key={s} value={s}>{s}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Employee</TableCell>
                <TableCell>Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>View</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {reports.map((r) => (
                <TableRow key={r.id}>
                  <TableCell>{getEmployeeName(r)}</TableCell>
                  <TableCell>
                    {r.createdAt
                      ? new Date(r.createdAt).toLocaleString('tr-TR')
                      : r.date}
                  </TableCell>
                  <TableCell>{r.status || 'Submitted'}</TableCell>
                  <TableCell>
                    <Button onClick={() => handleViewClick(r)}>View</Button>
                  </TableCell>
                  <TableCell>
                    {(user.id === r.userId || user.role === 'manager') && (
                      <>
                        <IconButton onClick={() => setEditingReport(r)}><EditIcon /></IconButton>
                        <IconButton onClick={() => handleDelete(r.id)}><DeleteIcon /></IconButton>
                      </>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
      {selectedReport && (
        <ReportDetailModal
          report={selectedReport}
          onClose={handleCloseModal}
        />
      )}
      <EditReportModal
        report={editingReport}
        onClose={() => setEditingReport(null)}
        onSave={handleUpdate}
      />
    </>
  );
}
