const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const delay = (ms) => new Promise((res) => setTimeout(res, ms));

export async function login(email, password) {
  await delay(500);
  if (email === 'manager@example.com') {
    localStorage.setItem('role', 'manager');
    return { role: 'manager', token: 'fake-jwt-token' };
  }
  localStorage.setItem('role', 'employee');
  return { role: 'employee', token: 'fake-jwt-token' };
}

export async function getMyReports() {
  await delay(500);
  return [
    { id: 1, title: 'Report 1', description: 'Did work', date: '2024-06-01' },
    { id: 2, title: 'Report 2', description: 'More work', date: '2024-06-02' },
  ];
}

export async function submitReport(report) {
  await delay(500);
  return { success: true, report: { ...report, id: Math.random() } };
}

export async function getAllReports({ date, employee }) {
  await delay(500);
  return [
    {
      id: 1,
      employee: 'Alice',
      title: 'Report 1',
      description: 'Did work',
      date: '2024-06-01',
    },
    {
      id: 2,
      employee: 'Bob',
      title: 'Report 2',
      description: 'More work',
      date: '2024-06-02',
    },
  ].filter(
    (r) =>
      (!date || r.date === date) &&
      (!employee || r.employee === employee)
  );
}
